import React from 'react';


const InputArea = (props) => {
    return(
        <div className="form">
          <input 
            type="text" 
            placeholder='Enter New Item'
            onChange={() => {
                props.handleChange(event);
            }}
            value={props.enteredItem}
          />
          <button onClick={props.isClicked}>
            <span>Add</span>
          </button>
        </div>
    );
}

export default InputArea;
